mapper.select:
select c.cartNo, c.productOptionNo, c.userNo, c.buyAmount, c.cartTime, p.categoryNo, p.productNo, p.name, po.productOptionName, po.productAmount, po.addPrice, p.basicPrice , po.addPrice+p.basicPrice as eachPrice from cart c, productOption po, product p where c.cartNo in
#{cartNo}
and c.productOptionNo = po.productOptionNo and p.productNo=(select productNo from productOption where productOptionNo= c.productOptionNo) order by (select productNo from productOption where productOptionNo= c.productOptionNo), productOptionNo asc ,buyAmount desc

mapper.update:
update cart
set buyAmount = #{buyAmount}
where cartNo = #{cartNo}
