mapper.select:
select po.productOptionNo, p.categoryNo, p.productNo, p.name, po.productOptionName, po.productAmount, po.addPrice, p.basicPrice , po.addPrice+p.basicPrice as eachPrice from productOption po, product p where po.productOptionNo in
#{productOptionNo}
AND p.productNo = po.productNo

mapper.update:
update productOption
set productAmount = #{productAmount}
where productOptionNo = #{productOptionNo}

update productOption
set productOptionName = #{productOptionName}, optionProcedure = #{optionProcedure}, addPrice = #{addPrice}, productAmount = #{productAmount}, optionStat = #{optionStat}
where productOptionNo = #{productOptionNo}
